import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  Card, 
  Row, 
  Col, 
  Button, 
  Space, 
  Tag, 
  Table, 
  message,
  Tabs,
  Statistic,
  Progress,
  Modal,
  Form,
  Input,
  Select,
  Upload,
  Divider,
  Typography,
  Steps,
  Collapse,
  Tooltip,
  Popconfirm
} from 'antd';
import { 
  ArrowLeftOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  ReloadOutlined,
  ExperimentOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ClockCircleOutlined,
  UploadOutlined,
  EditOutlined,
  CopyOutlined,
  DeleteOutlined,
  FileTextOutlined,
  SettingOutlined,
  CloudServerOutlined,
  InfoCircleOutlined
} from '@ant-design/icons';
import { 
  testTaskService, 
  testStrategyService, 
  testExecutionService,
  loadGeneratorService 
} from '../services/api';
import type { 
  TestTask, 
  TestTaskWithScenarios, 
  TestExecution, 
  TestStrategy,
  TestExecutionCreate
} from '../types/testTask';
import type { LoadGenerator, LoadGeneratorConfig } from '../types/loadGenerator';

const { Title, Text } = Typography;
const { Step } = Steps;
const { Panel } = Collapse;

const TestTaskDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [testTask, setTestTask] = useState<TestTaskWithScenarios | null>(null);
  const [executions, setExecutions] = useState<TestExecution[]>([]);
  const [strategies, setStrategies] = useState<TestStrategy[]>([]);
  const [loadGenerators, setLoadGenerators] = useState<LoadGenerator[]>([]);
  const [loadGeneratorConfigs, setLoadGeneratorConfigs] = useState<LoadGeneratorConfig[]>([]);
  const [loading, setLoading] = useState(false);

  // 场景创建相关状态
  const [scenarioModalVisible, setScenarioModalVisible] = useState(false);
  const [executionModalVisible, setExecutionModalVisible] = useState(false);
  const [editorModalVisible, setEditorModalVisible] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [scenarioForm] = Form.useForm();
  const [executionForm] = Form.useForm();

  // 场景配置状态
  const [selectedStrategy, setSelectedStrategy] = useState<TestStrategy | null>(null);
  const [selectedLoadGenerator, setSelectedLoadGenerator] = useState<LoadGenerator | null>(null);
  const [selectedConfig, setSelectedConfig] = useState<LoadGeneratorConfig | null>(null);
  const [uploadedFiles, setUploadedFiles] = useState<Array<{file: File, content: string, id: string}>>([]);
  const [selectedFileId, setSelectedFileId] = useState<string | null>(null);
  const [editorContent, setEditorContent] = useState('');
  const [isCreatingScenario, setIsCreatingScenario] = useState(false);
  const [currentScenario, setCurrentScenario] = useState<any>(null);
  const [scenarios, setScenarios] = useState<any[]>([]);

  useEffect(() => {
    if (id) {
      fetchTestTask();
      fetchExecutions();
      fetchStrategies();
      fetchLoadGenerators();
    }
  }, [id]);

  const fetchTestTask = async () => {
    if (!id) return;
    setLoading(true);
    try {
      const data = await testTaskService.getTestTask(parseInt(id));
      setTestTask(data);
    } catch (error) {
      message.error('Failed to fetch test task details');
    } finally {
      setLoading(false);
    }
  };

  const fetchExecutions = async () => {
    if (!id) return;
    try {
      const data = await testTaskService.getExecutions(parseInt(id));
      setExecutions(data);
    } catch (error) {
      message.error('Failed to fetch execution records');
    }
  };

  const fetchStrategies = async () => {
    try {
      const data = await testStrategyService.getTestStrategies();
      setStrategies(data);
    } catch (error) {
      message.error('Failed to fetch strategy list');
    }
  };

  const fetchLoadGenerators = async () => {
    try {
      const data = await loadGeneratorService.getLoadGenerators();
      setLoadGenerators(data);
    } catch (error) {
      message.error('Failed to fetch load generator list');
    }
  };

  const loadLoadGeneratorConfigs = async (loadGeneratorId: number) => {
    try {
      const configs = await loadGeneratorService.getConfigs(loadGeneratorId);
      setLoadGeneratorConfigs(configs);
    } catch (error) {
      message.error('Failed to load load generator configs');
    }
  };

  // 场景创建相关处理函数
  const handleCreateScenario = () => {
    const newScenario = {
      id: Date.now(), // 临时ID
      name: `Scenario ${scenarios.length + 1}`,
      interface_name: '',
      interface_url: '',
      method: 'GET',
      weight: 1,
      headers: {},
      body: '',
      timeout: 30,
      isNew: true
    };

    setScenarios([...scenarios, newScenario]);
    setCurrentScenario(newScenario);
    setIsCreatingScenario(false);
    setCurrentStep(0);
    setSelectedStrategy(null);
    setSelectedLoadGenerator(null);
    setSelectedConfig(null);
    setUploadedFiles([]);
    setSelectedFileId(null);
    setEditorContent('');
  };

  const handleSelectScenario = (scenario: any) => {
    setCurrentScenario(scenario);
    setIsCreatingScenario(false);
    setCurrentStep(0);
  };

  const handleCancelScenario = () => {
    setIsCreatingScenario(false);
    setCurrentStep(0);
    setSelectedStrategy(null);
    setSelectedLoadGenerator(null);
    setSelectedConfig(null);
    setUploadedFiles([]);
    setSelectedFileId(null);
    setEditorContent('');
  };

  const handleSaveScenario = () => {
    if (!currentScenario) return;

    // 这里可以调用API保存场景
    message.success('Scenario saved successfully');

    // 更新场景列表中的场景状态
    setScenarios(scenarios.map(s => 
      s.id === currentScenario.id 
        ? { ...currentScenario, isNew: false }
        : s
    ));
  };

  const handleDeleteScenario = (scenarioId: number) => {
    // 从本地场景列表中删除
    setScenarios(scenarios.filter(s => s.id !== scenarioId));

    // 如果删除的是当前选中的场景，清空当前场景
    if (currentScenario?.id === scenarioId) {
      setCurrentScenario(null);
    }

    message.success('Scenario deleted successfully');
  };

  const handleCloneScenario = (scenario: any) => {
    setScenarioModalVisible(true);
    setCurrentStep(0);
    scenarioForm.setFieldsValue(scenario);
    message.info('Scenario cloned, please modify as needed');
  };

  const handleFileUpload = (file: File) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target?.result as string || '';
      const fileId = Date.now().toString() + Math.random().toString(36).substr(2, 9);
      const newFile = {
        file,
        content,
        id: fileId
      };

      setUploadedFiles(prev => [...prev, newFile]);

      // 如果是第一个文件，自动选中
      if (uploadedFiles.length === 0) {
        setSelectedFileId(fileId);
        setEditorContent(content);
      }
    };
    reader.readAsText(file);
    return false; // 阻止自动上传
  };

  const handleFileSelect = (fileId: string) => {
    setSelectedFileId(fileId);
    const selectedFile = uploadedFiles.find(f => f.id === fileId);
    if (selectedFile) {
      setEditorContent(selectedFile.content);
    }
  };

  const handleFileDelete = (fileId: string) => {
    setUploadedFiles(prev => prev.filter(f => f.id !== fileId));

    // 如果删除的是当前选中的文件，选择其他文件或清空
    if (selectedFileId === fileId) {
      const remainingFiles = uploadedFiles.filter(f => f.id !== fileId);
      if (remainingFiles.length > 0) {
        const newSelectedId = remainingFiles[0].id;
        setSelectedFileId(newSelectedId);
        setEditorContent(remainingFiles[0].content);
      } else {
        setSelectedFileId(null);
        setEditorContent('');
      }
    }
  };

  const handleContentUpdate = (content: string) => {
    setEditorContent(content);
    if (selectedFileId) {
      setUploadedFiles(prev => prev.map(f => 
        f.id === selectedFileId ? { ...f, content } : f
      ));
    }
  };

  const handleEditorSave = () => {
    setEditorModalVisible(false);
    message.success('Script saved successfully');
  };

  const handleNextStep = () => {
    setCurrentStep(currentStep + 1);
  };

  const handlePrevStep = () => {
    setCurrentStep(currentStep - 1);
  };

  const handleExecuteScenario = async () => {
    if (!selectedStrategy || !selectedLoadGenerator || !selectedConfig) {
      message.error('Please complete all configuration steps');
      return;
    }

    try {
      const executionData: TestExecutionCreate = {
        task_id: parseInt(id!),
        strategy_id: selectedStrategy.id,
        load_generator_id: selectedLoadGenerator.id,
        load_generator_config_id: selectedConfig.id,
        execution_name: `Execution_${new Date().toLocaleString()}`
      };

      await testExecutionService.createTestExecution(executionData);
      message.success('Test execution created successfully');
      setIsCreatingScenario(false);
      setCurrentStep(0);
      fetchExecutions();
    } catch (error) {
      message.error('Failed to create test execution');
    }
  };

  const handleStart = () => {
    setExecutionModalVisible(true);
  };

  const handleCreateExecution = async (values: any) => {
    if (!id) return;
    try {
      const executionData: TestExecutionCreate = {
        task_id: parseInt(id),
        strategy_id: values.strategy_id,
        load_generator_id: values.load_generator_id,
        load_generator_config_id: values.load_generator_config_id,
        execution_name: values.execution_name || `Execution_${new Date().toLocaleString()}`
      };

      await testExecutionService.createTestExecution(executionData);
      message.success('Test execution created successfully');
      setExecutionModalVisible(false);
      fetchExecutions();
    } catch (error) {
      message.error('Failed to create test execution');
    }
  };


  const getStatusTag = (status: string) => {
    const statusMap = {
      pending: { color: 'default', text: 'Pending' },
      running: { color: 'processing', text: 'Running' },
      completed: { color: 'success', text: 'Completed' },
      failed: { color: 'error', text: 'Failed' },
      cancelled: { color: 'warning', text: 'Cancelled' }
    };
    const config = statusMap[status as keyof typeof statusMap] || { color: 'default', text: status };
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  const getScenarioTypeTag = (scenarioType: string) => {
    const typeMap = {
      single: { color: 'blue', text: 'Single Interface' },
      multi: { color: 'green', text: 'Multi Interface' }
    };
    const config = typeMap[scenarioType as keyof typeof typeMap] || { color: 'default', text: scenarioType };
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  const executionColumns = [
    {
      title: 'Execution Name',
      dataIndex: 'execution_name',
      key: 'execution_name',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => getStatusTag(status),
    },
    {
      title: 'Total Requests',
      dataIndex: 'total_requests',
      key: 'total_requests',
    },
    {
      title: 'Failures',
      dataIndex: 'total_failures',
      key: 'total_failures',
    },
    {
      title: 'Avg Response Time (ms)',
      dataIndex: 'avg_response_time',
      key: 'avg_response_time',
      render: (time: number) => time.toFixed(2),
    },
    {
      title: 'RPS',
      dataIndex: 'requests_per_second',
      key: 'requests_per_second',
      render: (rps: number) => rps.toFixed(2),
    },
    {
      title: 'Error Rate',
      dataIndex: 'error_rate',
      key: 'error_rate',
      render: (rate: number) => `${(rate * 100).toFixed(2)}%`,
    },
    {
      title: 'Started At',
      dataIndex: 'started_at',
      key: 'started_at',
      render: (date: string) => date ? new Date(date).toLocaleString() : '-',
    },
    {
      title: 'Completed At',
      dataIndex: 'completed_at',
      key: 'completed_at',
      render: (date: string) => date ? new Date(date).toLocaleString() : '-',
    },
    {
      title: 'Actions',
      key: 'action',
      render: (_: any, record: TestExecution) => (
        <Space>
          {record.status === 'pending' && (
            <Button
              size="small"
              type="primary"
              icon={<PlayCircleOutlined />}
              onClick={() => {
                testExecutionService.startTestExecution(record.id).then(() => {
                  message.success('Execution started');
                  fetchExecutions();
                }).catch(() => {
                  message.error('Failed to start execution');
                });
              }}
            >
              Start
            </Button>
          )}
          {record.status === 'running' && (
            <Button
              size="small"
              danger
              icon={<PauseCircleOutlined />}
              onClick={() => {
                testExecutionService.stopTestExecution(record.id, { reason: 'Manual stop' }).then(() => {
                  message.success('Execution stopped');
                  fetchExecutions();
                }).catch(() => {
                  message.error('Failed to stop execution');
                });
              }}
            >
              Stop
            </Button>
          )}
          <Button
            size="small"
            icon={<ReloadOutlined />}
            onClick={() => navigate(`/test-executions/${record.id}`)}
          >
            View Details
          </Button>
        </Space>
      ),
    },
  ];

  if (!testTask) {
    return <div>Loading...</div>;
  }

  const latestExecution = executions[0];
  const successRate = latestExecution ? 
    ((latestExecution.total_requests - latestExecution.total_failures) / latestExecution.total_requests * 100) : 0;

  // 场景创建步骤内容
  const renderScenarioSteps = () => {
    const steps = [
      {
        title: 'Script Upload',
        content: (
          <div>
            <Title level={4} style={{ color: '#fff', marginBottom: '20px' }}>Upload Locust Scripts</Title>
            <Row gutter={16} style={{ height: '500px' }}>
              {/* 左侧文件列表 */}
              <Col span={8}>
                <div style={{ 
                  background: '#333', 
                  borderRadius: '8px', 
                  padding: '16px',
                  height: '100%',
                  border: '1px solid #555'
                }}>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    marginBottom: '16px'
                  }}>
                    <Text style={{ color: '#fff', fontSize: '14px', fontWeight: 500 }}>Uploaded Files</Text>
                    <Upload
                      accept=".py"
                      beforeUpload={handleFileUpload}
                      showUploadList={false}
                      multiple
                    >
                      <Button 
                        icon={<UploadOutlined />} 
                        size="small"
                        style={{
                          background: '#1890ff',
                          borderColor: '#1890ff',
                          color: '#fff'
                        }}
                      >
                        Upload
                      </Button>
                    </Upload>
                  </div>

                  <div style={{ 
                    maxHeight: '400px', 
                    overflowY: 'auto',
                    border: '1px solid #555',
                    borderRadius: '6px',
                    background: '#2a2a2a'
                  }}>
                    {uploadedFiles.length === 0 ? (
                      <div style={{ 
                        padding: '20px', 
                        textAlign: 'center',
                        color: '#999'
                      }}>
                        <FileTextOutlined style={{ fontSize: '24px', marginBottom: '8px' }} />
                        <div>No files uploaded</div>
                        <div style={{ fontSize: '12px', marginTop: '4px' }}>
                          Click Upload to add Python files
                        </div>
                      </div>
                    ) : (
                      uploadedFiles.map((fileData) => (
                        <div
                          key={fileData.id}
                          onClick={() => handleFileSelect(fileData.id)}
                          style={{
                            padding: '12px 16px',
                            cursor: 'pointer',
                            borderBottom: '1px solid #444',
                            background: selectedFileId === fileData.id ? '#1890ff' : 'transparent',
                            color: selectedFileId === fileData.id ? '#fff' : '#ccc',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center'
                          }}
                        >
                          <div style={{ flex: 1, minWidth: 0 }}>
                            <div style={{ 
                              fontSize: '13px', 
                              fontWeight: 500,
                              overflow: 'hidden',
                              textOverflow: 'ellipsis',
                              whiteSpace: 'nowrap'
                            }}>
                              {fileData.file.name}
                            </div>
                            <div style={{ 
                              fontSize: '11px', 
                              opacity: 0.7,
                              marginTop: '2px'
                            }}>
                              {(fileData.file.size / 1024).toFixed(1)} KB
                            </div>
                          </div>
                          <Button
                            type="text"
                            size="small"
                            icon={<DeleteOutlined />}
                            onClick={(e) => {
                              e.stopPropagation();
                              handleFileDelete(fileData.id);
                            }}
                            style={{ 
                              color: selectedFileId === fileData.id ? '#fff' : '#999',
                              opacity: 0.7
                            }}
                          />
                        </div>
                      ))
                    )}
                  </div>

                  <Button 
                    type="dashed" 
                    icon={<FileTextOutlined />}
                    onClick={() => setEditorModalVisible(true)}
                    style={{ 
                      width: '100%',
                      marginTop: '12px',
                      borderColor: '#555',
                      color: '#fff',
                      background: '#2a2a2a'
                    }}
                  >
                    Create New Script
                  </Button>
                </div>
              </Col>

              {/* 右侧脚本内容展示 */}
              <Col span={16}>
                <div style={{ 
                  background: '#333', 
                  borderRadius: '8px', 
                  padding: '16px',
                  height: '100%',
                  border: '1px solid #555'
                }}>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    marginBottom: '16px'
                  }}>
                    <Text style={{ color: '#fff', fontSize: '14px', fontWeight: 500 }}>
                      {selectedFileId ? 
                        uploadedFiles.find(f => f.id === selectedFileId)?.file.name || 'Script Content' : 
                        'Script Content'
                      }
                    </Text>
                    {selectedFileId && (
                      <Button 
                        type="link" 
                        icon={<EditOutlined />}
                        onClick={() => setEditorModalVisible(true)}
                        style={{ color: '#1890ff' }}
                      >
                        Edit
                      </Button>
                    )}
                  </div>

                  <div style={{ 
                    height: '400px',
                    border: '1px solid #555',
                    borderRadius: '6px',
                    background: '#1a1a1a',
                    overflow: 'auto'
                  }}>
                    {selectedFileId ? (
                      <pre style={{
                        margin: 0,
                        padding: '16px',
                        color: '#fff',
                        fontSize: '13px',
                        lineHeight: '1.5',
                        fontFamily: 'Monaco, Menlo, "Ubuntu Mono", monospace',
                        whiteSpace: 'pre-wrap',
                        wordBreak: 'break-word'
                      }}>
                        {editorContent}
                      </pre>
                    ) : (
                      <div style={{ 
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        height: '100%',
                        color: '#999'
                      }}>
                        <FileTextOutlined style={{ fontSize: '48px', marginBottom: '16px' }} />
                        <div style={{ fontSize: '16px', marginBottom: '8px' }}>No file selected</div>
                        <div style={{ fontSize: '12px' }}>
                          Select a file from the left panel to view its content
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </Col>
            </Row>
          </div>
        )
      },
      {
        title: 'Strategy Configuration',
        content: (
          <div>
            <Title level={4} style={{ color: '#fff', marginBottom: '20px' }}>Select Test Strategy</Title>
            <Row gutter={16}>
              {strategies.map(strategy => (
                <Col span={8} key={strategy.id}>
                  <Card
                    hoverable
                    style={{ 
                      border: selectedStrategy?.id === strategy.id ? '2px solid #1890ff' : '1px solid #555',
                      cursor: 'pointer',
                      background: '#333',
                      borderRadius: '8px'
                    }}
                    bodyStyle={{
                      background: '#333',
                      color: '#fff'
                    }}
                    onClick={() => setSelectedStrategy(strategy)}
                  >
                    <div style={{ textAlign: 'center' }}>
                      <SettingOutlined style={{ fontSize: 24, color: '#1890ff', marginBottom: 8 }} />
                      <Title level={5} style={{ color: '#fff' }}>{strategy.name}</Title>
                      <Text style={{ color: '#ccc' }}>{strategy.strategy_type}</Text>
                      <div style={{ marginTop: 8 }}>
                        <Text style={{ color: '#fff' }}>Users: {strategy.user_count}</Text><br/>
                        <Text style={{ color: '#fff' }}>Spawn Rate: {strategy.spawn_rate}/s</Text><br/>
                        <Text style={{ color: '#fff' }}>Run Time: {strategy.run_time}s</Text>
                      </div>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </div>
        )
      },
      {
        title: 'Load Generator Configuration',
        content: (
          <div>
            <Title level={4} style={{ color: '#fff', marginBottom: '20px' }}>Select Load Generator & Configuration</Title>
            <Row gutter={16}>
              <Col span={12}>
                <Title level={5} style={{ color: '#fff', marginBottom: '12px' }}>Load Generators</Title>
                <Space direction="vertical" style={{ width: '100%' }} size="small">
                  {loadGenerators.map(lg => (
                    <Card
                      key={lg.id}
                      size="small"
                      hoverable
                      style={{ 
                        border: selectedLoadGenerator?.id === lg.id ? '2px solid #1890ff' : '1px solid #555',
                        cursor: 'pointer',
                        background: '#333',
                        borderRadius: '6px'
                      }}
                      bodyStyle={{
                        background: '#333',
                        color: '#fff',
                        padding: '12px'
                      }}
                      onClick={() => {
                        setSelectedLoadGenerator(lg);
                        loadLoadGeneratorConfigs(lg.id);
                      }}
                    >
                      <Space>
                        <CloudServerOutlined style={{ color: '#1890ff' }} />
                        <div>
                          <Text strong style={{ color: '#fff' }}>{lg.name}</Text><br/>
                          <Text style={{ color: '#ccc' }}>{lg.host}:{lg.port}</Text>
                        </div>
                      </Space>
                    </Card>
                  ))}
                </Space>
              </Col>
              <Col span={12}>
                <Title level={5} style={{ color: '#fff', marginBottom: '12px' }}>Configurations</Title>
                <Space direction="vertical" style={{ width: '100%' }} size="small">
                  {loadGeneratorConfigs.map(config => (
                    <Card
                      key={config.id}
                      size="small"
                      hoverable
                      style={{ 
                        border: selectedConfig?.id === config.id ? '2px solid #1890ff' : '1px solid #555',
                        cursor: 'pointer',
                        background: '#333',
                        borderRadius: '6px'
                      }}
                      bodyStyle={{
                        background: '#333',
                        color: '#fff',
                        padding: '12px'
                      }}
                      onClick={() => setSelectedConfig(config)}
                    >
                      <Space>
                        <InfoCircleOutlined style={{ color: '#1890ff' }} />
                        <div>
                          <Text strong style={{ color: '#fff' }}>{config.config_name}</Text><br/>
                          <Text style={{ color: '#ccc' }}>CPU: {config.system_cpu_cores} cores, Memory: {config.system_memory_gb}GB</Text>
                        </div>
                      </Space>
                    </Card>
                  ))}
                </Space>
              </Col>
            </Row>
          </div>
        )
      },
      {
        title: 'Execute',
        content: (
          <div style={{ textAlign: 'center', padding: '40px 0' }}>
            <Title level={4} style={{ color: '#fff', marginBottom: '30px' }}>Ready to Execute</Title>
            <Space direction="vertical" size="large">
              <div style={{ 
                background: '#333', 
                padding: '20px', 
                borderRadius: '8px',
                border: '1px solid #555',
                textAlign: 'left',
                maxWidth: '400px',
                margin: '0 auto'
              }}>
                <Text style={{ color: '#fff', display: 'block', marginBottom: '8px' }}>
                  <strong>Strategy:</strong> {selectedStrategy?.name}
                </Text>
                <Text style={{ color: '#fff', display: 'block', marginBottom: '8px' }}>
                  <strong>Load Generator:</strong> {selectedLoadGenerator?.name}
                </Text>
                <Text style={{ color: '#fff', display: 'block' }}>
                  <strong>Configuration:</strong> {selectedConfig?.config_name}
                </Text>
              </div>
              <Button 
                type="primary" 
