import React, { useState } from 'react';
import { Layout as AntLayout, Button, Avatar, Dropdown } from 'antd';
import { 
  DashboardOutlined, 
  UserOutlined,
  LogoutOutlined,
  MenuFoldOutlined,
  MenuUnfoldOutlined,
  RocketOutlined,
  CalendarOutlined,
  BellOutlined,
  SettingOutlined,
  BarChartOutlined,
  FileTextOutlined,
  UploadOutlined,
  DatabaseOutlined,
  CloudServerOutlined,
  ApiOutlined,
  MonitorOutlined,
  HistoryOutlined,
  CodeOutlined,
  TagsOutlined,
  KeyOutlined,
  DownOutlined,
  QuestionCircleOutlined
} from '@ant-design/icons';
import { useNavigate, useLocation } from 'react-router-dom';

const { Header, Sider, Content } = AntLayout;

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [collapsed, setCollapsed] = useState(false);
  const [expandedMenus, setExpandedMenus] = useState<string[]>([]);
  const navigate = useNavigate();
  const location = useLocation();

  const menuItems = [
    {
      key: 'load-data',
      icon: <UploadOutlined />,
      label: 'Load Data',
      hasSubMenu: true,
      subItems: [
        { key: '/load-generators', icon: <DatabaseOutlined />, label: 'Sources' },
        { key: '/buckets', icon: <CloudServerOutlined />, label: 'Buckets' },
        { key: '/telegrafs', icon: <MonitorOutlined />, label: 'Telegraf' },
        { key: '/scrapers', icon: <ApiOutlined />, label: 'Scrapers' },
        { key: '/tokens', icon: <KeyOutlined />, label: 'API Tokens' },
      ]
    },
    {
      key: '/',
      icon: <BarChartOutlined />,
      label: 'Data Explorer',
    },
    {
      key: '/notebooks',
      icon: <FileTextOutlined />,
      label: 'Notebooks',
    },
    {
      key: '/dashboards',
      icon: <DashboardOutlined />,
      label: 'Dashboards',
    },
    {
      key: '/test-management',
      icon: <CalendarOutlined />,
      label: 'Tasks',
    },
    {
      key: 'alerts',
      icon: <BellOutlined />,
      label: 'Alerts',
      hasSubMenu: true,
      subItems: [
        { key: '/alerts', icon: <BellOutlined />, label: 'Alerts' },
        { key: '/alert-history', icon: <HistoryOutlined />, label: 'Alert History' },
      ]
    },
    {
      key: 'settings',
      icon: <SettingOutlined />,
      label: 'Settings',
      hasSubMenu: true,
      subItems: [
        { key: '/variables', icon: <CodeOutlined />, label: 'Variables' },
        { key: '/templates', icon: <FileTextOutlined />, label: 'Templates' },
        { key: '/labels', icon: <TagsOutlined />, label: 'Labels' },
        { key: '/secrets', icon: <KeyOutlined />, label: 'Secrets' },
      ]
    },
  ];

  const userMenuItems = [
    {
      key: 'profile',
      icon: <UserOutlined />,
      label: '个人资料',
    },
    {
      key: 'logout',
      icon: <LogoutOutlined />,
      label: '退出登录',
    },
  ];

  const handleMenuClick = (item: any) => {
    if (item.hasSubMenu) {
      // 切换子菜单展开状态
      setExpandedMenus(prev => 
        prev.includes(item.key) 
          ? prev.filter(key => key !== item.key)
          : [...prev, item.key]
      );
    } else {
      navigate(item.key);
    }
  };

  const handleSubMenuClick = (key: string) => {
    navigate(key);
  };

  const handleUserMenuClick = ({ key }: { key: string }) => {
    if (key === 'logout') {
      // 处理退出登录
      console.log('退出登录');
    }
  };

  return (
    <AntLayout style={{ minHeight: '100vh', background: 'var(--bg-primary)' }}>
      <Sider 
        trigger={null} 
        collapsible 
        collapsed={collapsed}
        style={{
          background: '#0f0f23',
          borderRight: 'none',
          display: 'flex',
          flexDirection: 'column',
          width: collapsed ? 80 : 240,
          minWidth: collapsed ? 80 : 240,
          maxWidth: collapsed ? 80 : 240
        }}
      >
        {/* Logo区域 */}
        <div style={{ 
          height: 64, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: collapsed ? 'center' : 'flex-start',
          padding: collapsed ? '0' : '0 20px',
          borderBottom: '1px solid #2d3748',
          background: 'linear-gradient(135deg, #4c63d2 0%, #5a67d8 100%)'
        }}>
          {collapsed ? (
            <div style={{
              width: 32,
              height: 32,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <img 
                src="/favicon.ico" 
                alt="Platform Logo" 
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'contain'
                }}
              />
            </div>
          ) : (
            <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
              <div style={{
                width: 32,
                height: 32,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <img 
                  src="/favicon.ico" 
                  alt="Platform Logo" 
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'contain'
                  }}
                />
              </div>
              <span style={{
                color: '#fff',
                fontSize: '18px',
                fontWeight: 600,
                fontFamily: 'system-ui, -apple-system, sans-serif'
              }}>
                pfp™
              </span>
            </div>
          )}
        </div>

        {/* 用户信息区域 */}
        <div style={{
          padding: collapsed ? '16px 8px' : '16px 20px',
          borderBottom: '1px solid #2d3748'
        }}>
          {collapsed ? (
            <div style={{
              width: 40,
              height: 40,
              background: 'linear-gradient(135deg, #8b5cf6 0%, #ec4899 100%)',
              borderRadius: '8px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: '#fff',
              fontSize: '16px',
              fontWeight: 'bold',
              margin: '0 auto'
            }}>
              微
            </div>
          ) : (
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              background: 'transparent',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.2s ease'
            }}>
              <div style={{
                width: 40,
                height: 40,
                background: 'linear-gradient(135deg, #8b5cf6 0%, #ec4899 100%)',
                borderRadius: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: '#fff',
                fontSize: '16px',
                fontWeight: 'bold'
              }}>
                微
              </div>
              <div>
                <div style={{ color: '#fff', fontSize: '14px', fontWeight: 500 }}>admin</div>
                <div style={{ color: '#a0aec0', fontSize: '12px' }}>pfp</div>
              </div>
            </div>
          )}
        </div>

        {/* 主菜单区域 */}
        <div style={{ padding: '16px 0', flex: 1, overflowY: 'auto', display: 'flex', flexDirection: 'column' }}>
          <div style={{ flex: 1 }}>
            {menuItems.map(item => {
              const isExpanded = expandedMenus.includes(item.key);
              const hasActiveSubItem = item.subItems?.some(subItem => location.pathname === subItem.key);
              const isActive = location.pathname === item.key || hasActiveSubItem;

              return (
                <div key={item.key}>
                  {/* 主菜单项 */}
                  <div
                    onClick={() => handleMenuClick(item)}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      padding: collapsed ? '12px 20px' : '12px 20px',
                      color: isActive ? '#fff' : '#a0aec0',
                      cursor: 'pointer',
                      transition: 'all 0.2s ease',
                      background: isActive ? 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)' : 'transparent',
                      margin: '2px 0',
                      borderRadius: '0 8px 8px 0',
                      marginRight: '8px',
                      borderLeft: isActive ? '3px solid #8b5cf6' : '3px solid transparent'
                    }}
                  >
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ fontSize: '16px', minWidth: '20px' }}>{item.icon}</span>
                      {!collapsed && <span style={{ marginLeft: '12px', fontSize: '14px', fontWeight: 500 }}>{item.label}</span>}
                    </div>
                    {!collapsed && item.hasSubMenu && (
                      <span style={{ 
                        fontSize: '12px', 
                        transition: 'transform 0.2s ease',
                        transform: isExpanded ? 'rotate(180deg)' : 'rotate(0deg)'
                      }}>
                        <DownOutlined />
                      </span>
                    )}
                  </div>

                  {/* 子菜单 */}
                  {!collapsed && item.hasSubMenu && isExpanded && (
                    <div style={{ marginLeft: '20px', marginTop: '4px' }}>
                      {item.subItems?.map(subItem => (
                        <div
                          key={subItem.key}
                          onClick={() => handleSubMenuClick(subItem.key)}
                          style={{
                            display: 'flex',
                            alignItems: 'center',
                            padding: '8px 16px',
                            color: location.pathname === subItem.key ? '#fff' : '#a0aec0',
                            cursor: 'pointer',
                            transition: 'all 0.2s ease',
                            background: location.pathname === subItem.key ? 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)' : 'transparent',
                            margin: '2px 0',
                            borderRadius: '0 6px 6px 0',
                            marginRight: '8px',
                            fontSize: '13px'
                          }}
                        >
                          <span style={{ fontSize: '14px', minWidth: '16px', marginRight: '8px' }}>{subItem.icon}</span>
                          <span style={{ fontSize: '13px', fontWeight: 400 }}>{subItem.label}</span>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {/* Help & Support - 在主菜单区域内，但靠近底部 */}
          <div
            onClick={() => console.log('Help & Support')}
            style={{
              display: 'flex',
              alignItems: 'center',
              padding: collapsed ? '12px 20px' : '12px 20px',
              color: '#a0aec0',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
              margin: '2px 0',
              borderRadius: '0 8px 8px 0',
              marginRight: '8px',
              background: 'transparent',
              marginTop: 'auto'
            }}
          >
            <span style={{ fontSize: '16px', minWidth: '20px' }}><QuestionCircleOutlined /></span>
            {!collapsed && <span style={{ marginLeft: '12px', fontSize: '14px', fontWeight: 500 }}>Help & Support</span>}
          </div>
        </div>

        {/* 展开/收缩按钮 - 固定在Sider最底部 */}
        <div style={{ 
          padding: '16px 0',
          borderTop: '1px solid #2d3748',
          display: 'flex',
          justifyContent: 'center',
          marginTop: 'auto',
          flexShrink: 0
        }}>
          <Button
            type="text"
            icon={collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
            onClick={() => setCollapsed(!collapsed)}
            style={{ 
              fontSize: '14px', 
              width: 32, 
              height: 32,
              color: '#a0aec0',
              background: 'transparent',
              border: '1px solid #374151',
              borderRadius: '6px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          />
        </div>
      </Sider>
      <AntLayout>
        <Header style={{ 
          padding: '0 24px', 
          background: '#0f0f23',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'flex-end',
          borderBottom: '1px solid #374151'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
            <span style={{ 
              color: '#a0aec0', 
              fontSize: '12px', 
              fontWeight: 500,
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              Performance Platform v1.0
            </span>
            <Dropdown
              menu={{ 
                items: userMenuItems,
                onClick: handleUserMenuClick
              }}
              placement="bottomRight"
            >
              <Avatar 
                icon={<RocketOutlined />} 
                style={{ 
                  cursor: 'pointer',
                  background: 'linear-gradient(135deg, #8b5cf6 0%, #ec4899 100%)',
                  border: '1px solid #374151'
                }}
              />
            </Dropdown>
          </div>
        </Header>
        <Content style={{ 
          margin: 0, 
          minHeight: 280,
          background: '#0f0f23',
          padding: '24px'
        }}>
          {children}
        </Content>
      </AntLayout>
    </AntLayout>
  );
};

export default Layout;
