import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Button, Avatar, Dropdown, Typography } from 'antd';
import { 
  DashboardOutlined, 
  CloudServerOutlined, 
  ExperimentOutlined,
  UserOutlined,
  LogoutOutlined,
  MenuFoldOutlined,
  MenuUnfoldOutlined,
  RocketOutlined
} from '@ant-design/icons';
import { useNavigate, useLocation } from 'react-router-dom';

const { Title } = Typography;

const { Header, Sider, Content } = AntLayout;

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [collapsed, setCollapsed] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const menuItems = [
    {
      key: '/',
      icon: <DashboardOutlined />,
      label: 'COMMAND CENTER',
    },
    {
      key: '/load-generators',
      icon: <CloudServerOutlined />,
      label: 'LOAD GENERATORS',
    },
    {
      key: '/test-management',
      icon: <RocketOutlined />,
      label: 'TEST MANAGEMENT',
    },
  ];

  const userMenuItems = [
    {
      key: 'profile',
      icon: <UserOutlined />,
      label: '个人资料',
    },
    {
      key: 'logout',
      icon: <LogoutOutlined />,
      label: '退出登录',
    },
  ];

  const handleMenuClick = ({ key }: { key: string }) => {
    navigate(key);
  };

  const handleUserMenuClick = ({ key }: { key: string }) => {
    if (key === 'logout') {
      // 处理退出登录
      console.log('退出登录');
    }
  };

  return (
    <AntLayout style={{ minHeight: '100vh', background: 'var(--bg-primary)' }}>
      <Sider 
        trigger={null} 
        collapsible 
        collapsed={collapsed}
        style={{
          background: 'var(--bg-secondary)',
          borderRight: '1px solid var(--border)',
          display: 'flex',
          flexDirection: 'column'
        }}
      >
        <div style={{ 
          height: 64, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          borderBottom: '1px solid var(--border)',
          background: 'var(--bg-card)'
        }}>
          <Title level={3} style={{ 
            margin: 0, 
            color: 'var(--primary)',
            fontSize: collapsed ? 16 : 20,
            transition: 'all 0.2s',
            fontFamily: 'inherit',
            fontWeight: 700,
            letterSpacing: '1px'
          }}>
            {collapsed ? 'J' : 'JINX'}
          </Title>
        </div>
        <div style={{ padding: '16px 0', flex: 1 }}>
          {menuItems.map(item => (
            <div
              key={item.key}
              onClick={() => handleMenuClick({ key: item.key })}
              style={{
                display: 'flex',
                alignItems: 'center',
                padding: '12px 24px',
                color: location.pathname === item.key ? 'var(--primary)' : 'var(--text-secondary)',
                cursor: 'pointer',
                transition: 'all 0.2s ease',
                borderLeft: `3px solid ${location.pathname === item.key ? 'var(--primary)' : 'transparent'}`,
                background: location.pathname === item.key ? 'var(--bg-hover)' : 'transparent',
                margin: '4px 0'
              }}
            >
              <span style={{ fontSize: '18px' }}>{item.icon}</span>
              {!collapsed && <span style={{ marginLeft: '12px', fontSize: '14px', fontWeight: 500 }}>{item.label}</span>}
            </div>
          ))}
        </div>

        {/* 展开/收缩按钮放在菜单栏底部 */}
        <div style={{ 
          padding: '16px 0',
          borderTop: '1px solid var(--border)',
          display: 'flex',
          justifyContent: 'center'
        }}>
          <Button
            type="text"
            icon={collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
            onClick={() => setCollapsed(!collapsed)}
            style={{ 
              fontSize: '16px', 
              width: 40, 
              height: 40,
              color: 'var(--text-secondary)',
              background: 'transparent',
              border: 'none',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          />
        </div>
      </Sider>
      <AntLayout>
        <Header style={{ 
          padding: '0 24px', 
          background: 'var(--bg-secondary)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'flex-end',
          borderBottom: '1px solid var(--border)'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
            <span style={{ 
              color: 'var(--text-muted)', 
              fontSize: '12px', 
              fontWeight: 500,
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              Performance Platform v1.0
            </span>
            <Dropdown
              menu={{ 
                items: userMenuItems,
                onClick: handleUserMenuClick
              }}
              placement="bottomRight"
            >
              <Avatar 
                icon={<RocketOutlined />} 
                style={{ 
                  cursor: 'pointer',
                  background: 'var(--primary)',
                  border: '1px solid var(--border)'
                }}
              />
            </Dropdown>
          </div>
        </Header>
        <Content style={{ 
          margin: 0, 
          minHeight: 280,
          background: 'var(--bg-primary)',
          padding: '24px'
        }}>
          {children}
        </Content>
      </AntLayout>
    </AntLayout>
  );
};

export default Layout;
