import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Table, 
  Button, 
  Space, 
  Tag, 
  Modal, 
  Form, 
  Input, 
  InputNumber, 
  Select,
  message,
  Popconfirm,
  Card,
  Row,
  Col,
  Statistic,
  Typography
} from 'antd';
import { 
  PlusOutlined, 
  EditOutlined, 
  DeleteOutlined, 
  PlayCircleOutlined,
  PauseCircleOutlined,
  ReloadOutlined,
  ExperimentOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ClockCircleOutlined,
  RocketOutlined,
  ThunderboltOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { testTaskService, loadGeneratorService } from '../services/api';
import type { TestTask } from '../types/testTask';
import type { LoadGenerator } from '../types/loadGenerator';

const { Title } = Typography;
const { Option } = Select;

const TestTaskList: React.FC = () => {
  const [testTasks, setTestTasks] = useState<TestTask[]>([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [editingTask, setEditingTask] = useState<TestTask | null>(null);
  const [form] = Form.useForm();
  const [loadGenerators, setLoadGenerators] = useState<LoadGenerator[]>([]);

  const navigate = useNavigate();

  const fetchTestTasks = useCallback(async () => {
    setLoading(true);
    try {
      const data = await testTaskService.getTestTasks();
      setTestTasks(data);
    } catch (error) {
      message.error('获取测试任务列表失败');
    } finally {
      setLoading(false);
    }
  }, []);

  const fetchLoadGenerators = useCallback(async () => {
    try {
      const data = await loadGeneratorService.getLoadGenerators();
      setLoadGenerators(data);
    } catch (error) {
      console.error('Failed to fetch load generators:', error);
    }
  }, []);

  useEffect(() => {
    fetchTestTasks();
    fetchLoadGenerators();
  }, [fetchTestTasks, fetchLoadGenerators]);

  const handleAdd = useCallback(() => {
    setEditingTask(null);
    form.resetFields();
    setModalVisible(true);
  }, [form]);

  const handleEdit = useCallback((record: TestTask) => {
    setEditingTask(record);
    form.setFieldsValue(record);
    setModalVisible(true);
  }, [form]);

  const handleDelete = useCallback(async (id: number) => {
    try {
      await testTaskService.deleteTestTask(id);
      message.success('删除成功');
      fetchTestTasks();
    } catch (error) {
      message.error('删除失败');
    }
  }, [fetchTestTasks]);

  const handleCreateExecution = useCallback((id: number) => {
    navigate(`/test-tasks/${id}`);
  }, [navigate]);

  const handleSubmit = useCallback(async (values: any) => {
    try {
      if (editingTask) {
        await testTaskService.updateTestTask(editingTask.id, values);
        message.success('更新成功');
      } else {
        await testTaskService.createTestTask(values);
        message.success('创建成功');
      }
      setModalVisible(false);
      fetchTestTasks();
    } catch (error) {
      message.error(editingTask ? '更新失败' : '创建失败');
    }
  }, [editingTask, fetchTestTasks]);

  // 使用 useCallback 优化渲染函数
  const getScenarioTypeTag = useCallback((type: string) => {
    const typeMap = {
      single: { color: 'blue', text: '单接口' },
      multi: { color: 'green', text: '多接口' }
    };
    const config = typeMap[type as keyof typeof typeMap] || { color: 'default', text: type };
    return <Tag color={config.color}>{config.text}</Tag>;
  }, []);

  const columns = useMemo(() => [
    {
      title: 'TASK NAME',
      dataIndex: 'name',
      key: 'name',
      render: (text: string, record: TestTask) => (
        <Button 
          type="link" 
          onClick={() => navigate(`/test-tasks/${record.id}`)}
        >
          {text}
        </Button>
      ),
    },
    {
      title: 'TARGET HOST',
      dataIndex: 'target_host',
      key: 'target_host',
    },
    {
      title: 'SCENARIO TYPE',
      dataIndex: 'scenario_type',
      key: 'scenario_type',
      render: (type: string) => getScenarioTypeTag(type),
    },
    {
      title: 'CREATED AT',
      dataIndex: 'created_at',
      key: 'created_at',
      render: (date: string) => new Date(date).toLocaleString(),
    },
    {
      title: 'ACTIONS',
      key: 'action',
      render: (_: any, record: TestTask) => (
        <Space size="small">
          <Button 
            type="link" 
            icon={<PlayCircleOutlined />}
            onClick={() => handleCreateExecution(record.id)}
          >
            EXECUTE
          </Button>
          <Button 
            type="link" 
            icon={<EditOutlined />}
            onClick={() => handleEdit(record)}
          >
            EDIT
          </Button>
          <Popconfirm
            title="Delete this test task?"
            onConfirm={() => handleDelete(record.id)}
            okText="Yes"
            cancelText="No"
          >
            <Button type="link" danger icon={<DeleteOutlined />}>
              DELETE
            </Button>
          </Popconfirm>
        </Space>
      ),
    },
  ], [handleEdit, handleDelete, handleCreateExecution, navigate, getScenarioTypeTag]);

  // 统计数据 - 使用useMemo优化
  const stats = useMemo(() => ({
    total: testTasks.length,
    single: testTasks.filter(t => t.scenario_type === 'single').length,
    multi: testTasks.filter(t => t.scenario_type === 'multi').length,
  }), [testTasks]);

  return (
    <div style={{ background: 'var(--bg-primary)', minHeight: '100vh' }}>
      <div style={{ marginBottom: '32px' }}>
        <Title level={1} className="modern-title" style={{ 
          marginBottom: '8px',
          color: 'var(--text-primary)',
          fontSize: '32px',
          fontWeight: 700
        }}>
          BOMBARDMENT CENTER
        </Title>
        <p style={{ 
          color: 'var(--text-secondary)', 
          fontSize: '16px', 
          margin: 0, 
          fontFamily: 'inherit',
          fontWeight: 500
        }}>
          CREATE AND MANAGE YOUR BOMBARDMENT MISSIONS
        </p>
      </div>

      {/* 统计卡片 */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card className="modern-card" style={{ background: 'var(--bg-card)', border: '1px solid var(--border)' }}>
            <Statistic
              title={<span style={{ color: 'var(--text-muted)', fontSize: '12px', fontWeight: 500, textTransform: 'uppercase', letterSpacing: '0.5px' }}>TOTAL</span>}
              value={stats.total}
              prefix={<ExperimentOutlined style={{ color: 'var(--primary)' }} />}
              valueStyle={{ color: 'var(--text-primary)', fontWeight: 700, fontSize: '24px' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card className="modern-card" style={{ background: 'var(--bg-card)', border: '1px solid var(--border)' }}>
            <Statistic
              title={<span style={{ color: 'var(--text-muted)', fontSize: '12px', fontWeight: 500, textTransform: 'uppercase', letterSpacing: '0.5px' }}>SINGLE</span>}
              value={stats.single}
              valueStyle={{ color: 'var(--primary)', fontWeight: 700, fontSize: '24px' }}
              prefix={<ExperimentOutlined style={{ color: 'var(--primary)' }} />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card className="modern-card" style={{ background: 'var(--bg-card)', border: '1px solid var(--border)' }}>
            <Statistic
              title={<span style={{ color: 'var(--text-muted)', fontSize: '12px', fontWeight: 500, textTransform: 'uppercase', letterSpacing: '0.5px' }}>MULTI</span>}
              value={stats.multi}
              valueStyle={{ color: 'var(--success)', fontWeight: 700, fontSize: '24px' }}
              prefix={<ThunderboltOutlined style={{ color: 'var(--success)' }} />}
            />
          </Card>
        </Col>
      </Row>

      <div style={{ marginBottom: '24px' }}>
        <div style={{ display: 'flex', gap: '12px', marginBottom: '16px' }}>
          <Button 
            className="modern-button"
            icon={<PlusOutlined />}
            onClick={handleAdd}
            size="large"
            style={{ background: 'var(--primary)', borderColor: 'var(--primary)', color: 'white' }}
          >
            CREATE MISSION
          </Button>
          <Button 
            className="modern-button"
            icon={<ReloadOutlined />}
            onClick={fetchTestTasks}
            size="large"
            style={{ background: 'var(--bg-card)', borderColor: 'var(--border)', color: 'var(--text-primary)' }}
          >
            REFRESH
          </Button>
        </div>

        <Card 
          className="modern-card"
          style={{ background: 'var(--bg-card)', border: '1px solid var(--border)' }}
        >
          <Table
            columns={columns}
            dataSource={testTasks}
            rowKey="id"
            loading={loading}
            pagination={{
              showSizeChanger: true,
              showQuickJumper: true,
              showTotal: (total) => `TOTAL ${total} RECORDS`,
            }}
            style={{ 
              background: 'var(--bg-card)',
              color: 'var(--text-primary)'
            }}
            className="modern-table"
          />
        </Card>
      </div>

      {/* 添加/编辑模态框 - 自定义实现 */}
      {modalVisible && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          zIndex: 1000,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '20px',
          background: 'rgba(0, 0, 0, 0.3)',
          backdropFilter: 'blur(2px)'
        }}>
          <div style={{
            background: 'var(--bg-card)',
            border: '2px solid var(--primary)',
            borderRadius: '16px',
            boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.8)',
            width: '800px',
            maxWidth: '90vw',
            maxHeight: '90vh',
            overflow: 'auto'
          }}>
            {/* 模态框头部 */}
            <div style={{
              padding: '24px 24px 16px',
              borderBottom: '1px solid var(--border)',
              background: 'var(--bg-secondary)',
              borderRadius: '16px 16px 0 0',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <h3 style={{
                margin: 0,
                color: 'var(--text-primary)',
                fontWeight: 600,
                fontSize: '18px'
              }}>
                {editingTask ? 'EDIT MISSION' : 'CREATE MISSION'}
              </h3>
              <button
                onClick={() => setModalVisible(false)}
                style={{
                  background: 'var(--bg-hover)',
                  border: 'none',
                  borderRadius: '50%',
                  width: '32px',
                  height: '32px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  color: 'var(--text-primary)',
                  fontSize: '16px'
                }}
              >
                ×
              </button>
            </div>

            {/* 模态框内容 */}
            <div style={{ padding: '24px' }}>
              <Form
                form={form}
                layout="vertical"
                onFinish={handleSubmit}
              >
                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      name="name"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>MISSION NAME</span>}
                      rules={[{ required: true, message: 'Please enter mission name' }]}
                    >
                      <Input 
                        placeholder="Enter mission name" 
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }} 
                      />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item
                      name="target_host"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>TARGET HOST</span>}
                      rules={[{ required: true, message: 'Please enter target host' }]}
                    >
                      <Input 
                        placeholder="Enter target host" 
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }} 
                      />
                    </Form.Item>
                  </Col>
                </Row>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      name="load_generator_id"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>LOAD GENERATOR</span>}
                      rules={[{ required: true, message: 'Please select a load generator' }]}
                    >
                      <Select 
                        placeholder="Select a load generator" 
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }}
                      >
                        {loadGenerators.map(lg => (
                          <Option key={lg.id} value={lg.id}>{lg.name} ({lg.host})</Option>
                        ))}
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item
                      name="test_strategy"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>TEST STRATEGY</span>}
                      rules={[{ required: true, message: 'Please select a test strategy' }]}
                    >
                      <Select 
                        placeholder="Select test strategy" 
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }}
                      >
                        <Option value="single">Single Test</Option>
                        <Option value="progressive">Progressive Test</Option>
                        <Option value="adaptive">Adaptive Test</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                </Row>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      name="user_count"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>USER COUNT</span>}
                      rules={[{ required: true, message: 'Please enter user count' }]}
                    >
                      <InputNumber 
                        min={1} 
                        placeholder="100" 
                        defaultValue={100}
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }}
                      />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item
                      name="spawn_rate"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>SPAWN RATE</span>}
                      rules={[{ required: true, message: 'Please enter spawn rate' }]}
                    >
                      <InputNumber 
                        min={1} 
                        placeholder="10" 
                        defaultValue={10}
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }}
                      />
                    </Form.Item>
                  </Col>
                </Row>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      name="run_time"
                      label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>RUN TIME (minutes)</span>}
                      rules={[{ required: true, message: 'Please enter run time' }]}
                    >
                      <InputNumber 
                        min={1} 
                        placeholder="60" 
                        defaultValue={60}
                        style={{ 
                          width: '100%',
                          background: 'var(--bg-tertiary)',
                          border: '1px solid var(--border)',
                          color: 'var(--text-primary)'
                        }}
                      />
                    </Form.Item>
                  </Col>
                </Row>

                <Form.Item
                  name="description"
                  label={<span style={{ color: 'var(--text-secondary)', fontWeight: 500 }}>DESCRIPTION</span>}
                >
                  <Input.TextArea 
                    rows={3} 
                    placeholder="Enter mission description" 
                    style={{ 
                      width: '100%',
                      background: 'var(--bg-tertiary)',
                      border: '1px solid var(--border)',
                      color: 'var(--text-primary)'
                    }} 
                  />
                </Form.Item>
              </Form>
            </div>

            {/* 模态框底部 */}
            <div style={{
              padding: '16px 24px 24px',
              borderTop: '1px solid var(--border)',
              background: 'var(--bg-secondary)',
              borderRadius: '0 0 16px 16px',
              display: 'flex',
              gap: '12px',
              justifyContent: 'flex-end'
            }}>
              <Button 
                onClick={() => setModalVisible(false)} 
                style={{ 
                  background: 'var(--bg-card)', 
                  borderColor: 'var(--border)', 
                  color: 'var(--text-primary)' 
                }}
              >
                CANCEL
              </Button>
              <Button 
                type="primary" 
                loading={loading} 
                onClick={() => form.submit()} 
                style={{ 
                  background: 'var(--primary)', 
                  borderColor: 'var(--primary)', 
                  color: 'white' 
                }}
              >
                SAVE
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// 使用 React.memo 优化组件性能，避免不必要的重渲染
export default React.memo(TestTaskList);
