import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  Card, 
  Row, 
  Col, 
  Button, 
  Space, 
  Tag, 
  Table, 
  message,
  Tabs,
  Statistic,
  Progress,
  Modal,
  Form,
  Input,
  Select,
  Upload,
  Divider,
  Typography,
  Steps,
  Collapse,
  Tooltip,
  Popconfirm
} from 'antd';
import { 
  ArrowLeftOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  ReloadOutlined,
  ExperimentOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ClockCircleOutlined,
  UploadOutlined,
  EditOutlined,
  CopyOutlined,
  DeleteOutlined,
  FileTextOutlined,
  SettingOutlined,
  CloudServerOutlined,
  InfoCircleOutlined
} from '@ant-design/icons';
import { 
  testTaskService, 
  testStrategyService, 
  testExecutionService,
  loadGeneratorService 
} from '../services/api';
import type { 
  TestTask, 
  TestTaskWithScenarios, 
  TestExecution, 
  TestStrategy,
  TestExecutionCreate
} from '../types/testTask';
import type { LoadGenerator, LoadGeneratorConfig } from '../types/loadGenerator';

const { Title, Text } = Typography;
const { Step } = Steps;
const { Panel } = Collapse;

const TestTaskDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [testTask, setTestTask] = useState<TestTaskWithScenarios | null>(null);
  const [executions, setExecutions] = useState<TestExecution[]>([]);
  const [strategies, setStrategies] = useState<TestStrategy[]>([]);
  const [loadGenerators, setLoadGenerators] = useState<LoadGenerator[]>([]);
  const [loadGeneratorConfigs, setLoadGeneratorConfigs] = useState<LoadGeneratorConfig[]>([]);
  const [loading, setLoading] = useState(false);

  // 场景创建相关状态
  const [scenarioModalVisible, setScenarioModalVisible] = useState(false);
  const [executionModalVisible, setExecutionModalVisible] = useState(false);
  const [editorModalVisible, setEditorModalVisible] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [scenarioForm] = Form.useForm();
  const [executionForm] = Form.useForm();

  // 场景配置状态
  const [selectedStrategy, setSelectedStrategy] = useState<TestStrategy | null>(null);
  const [selectedLoadGenerator, setSelectedLoadGenerator] = useState<LoadGenerator | null>(null);
  const [selectedConfig, setSelectedConfig] = useState<LoadGeneratorConfig | null>(null);
  const [locustFile, setLocustFile] = useState<File | null>(null);
  const [editorContent, setEditorContent] = useState('');
  const [isCreatingScenario, setIsCreatingScenario] = useState(false);

  useEffect(() => {
    if (id) {
      fetchTestTask();
      fetchExecutions();
      fetchStrategies();
      fetchLoadGenerators();
    }
  }, [id]);

  const fetchTestTask = async () => {
    if (!id) return;
    setLoading(true);
    try {
      const data = await testTaskService.getTestTask(parseInt(id));
      setTestTask(data);
    } catch (error) {
      message.error('Failed to fetch test task details');
    } finally {
      setLoading(false);
    }
  };

  const fetchExecutions = async () => {
    if (!id) return;
    try {
      const data = await testTaskService.getExecutions(parseInt(id));
      setExecutions(data);
    } catch (error) {
      message.error('Failed to fetch execution records');
    }
  };

  const fetchStrategies = async () => {
    try {
      const data = await testStrategyService.getTestStrategies();
      setStrategies(data);
    } catch (error) {
      message.error('Failed to fetch strategy list');
    }
  };

  const fetchLoadGenerators = async () => {
    try {
      const data = await loadGeneratorService.getLoadGenerators();
      setLoadGenerators(data);
    } catch (error) {
      message.error('Failed to fetch load generator list');
    }
  };

  const loadLoadGeneratorConfigs = async (loadGeneratorId: number) => {
    try {
      const configs = await loadGeneratorService.getConfigs(loadGeneratorId);
      setLoadGeneratorConfigs(configs);
    } catch (error) {
      message.error('Failed to load load generator configs');
    }
  };

  // 场景创建相关处理函数
  const handleCreateScenario = () => {
    setIsCreatingScenario(true);
    setCurrentStep(0);
    setSelectedStrategy(null);
    setSelectedLoadGenerator(null);
    setSelectedConfig(null);
    setLocustFile(null);
    setEditorContent('');
  };

  const handleCancelScenario = () => {
    setIsCreatingScenario(false);
    setCurrentStep(0);
    setSelectedStrategy(null);
    setSelectedLoadGenerator(null);
    setSelectedConfig(null);
    setLocustFile(null);
    setEditorContent('');
  };

  const handleCloneScenario = (scenario: any) => {
    setScenarioModalVisible(true);
    setCurrentStep(0);
    scenarioForm.setFieldsValue(scenario);
    message.info('Scenario cloned, please modify as needed');
  };

  const handleFileUpload = (file: File) => {
    setLocustFile(file);
    const reader = new FileReader();
    reader.onload = (e) => {
      setEditorContent(e.target?.result as string || '');
    };
    reader.readAsText(file);
    return false; // 阻止自动上传
  };

  const handleEditorSave = () => {
    setEditorModalVisible(false);
    message.success('Script saved successfully');
  };

  const handleNextStep = () => {
    setCurrentStep(currentStep + 1);
  };

  const handlePrevStep = () => {
    setCurrentStep(currentStep - 1);
  };

  const handleExecuteScenario = async () => {
    if (!selectedStrategy || !selectedLoadGenerator || !selectedConfig) {
      message.error('Please complete all configuration steps');
      return;
    }

    try {
      const executionData: TestExecutionCreate = {
        task_id: parseInt(id!),
        strategy_id: selectedStrategy.id,
        load_generator_id: selectedLoadGenerator.id,
        load_generator_config_id: selectedConfig.id,
        execution_name: `Execution_${new Date().toLocaleString()}`
      };

      await testExecutionService.createTestExecution(executionData);
      message.success('Test execution created successfully');
      setIsCreatingScenario(false);
      setCurrentStep(0);
      fetchExecutions();
    } catch (error) {
      message.error('Failed to create test execution');
    }
  };

  const handleStart = () => {
    setExecutionModalVisible(true);
  };

  const handleCreateExecution = async (values: any) => {
    if (!id) return;
    try {
      const executionData: TestExecutionCreate = {
        task_id: parseInt(id),
        strategy_id: values.strategy_id,
        load_generator_id: values.load_generator_id,
        load_generator_config_id: values.load_generator_config_id,
        execution_name: values.execution_name || `Execution_${new Date().toLocaleString()}`
      };

      await testExecutionService.createTestExecution(executionData);
      message.success('Test execution created successfully');
      setExecutionModalVisible(false);
      fetchExecutions();
    } catch (error) {
      message.error('Failed to create test execution');
    }
  };


  const getStatusTag = (status: string) => {
    const statusMap = {
      pending: { color: 'default', text: 'Pending' },
      running: { color: 'processing', text: 'Running' },
      completed: { color: 'success', text: 'Completed' },
      failed: { color: 'error', text: 'Failed' },
      cancelled: { color: 'warning', text: 'Cancelled' }
    };
    const config = statusMap[status as keyof typeof statusMap] || { color: 'default', text: status };
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  const getScenarioTypeTag = (scenarioType: string) => {
    const typeMap = {
      single: { color: 'blue', text: 'Single Interface' },
      multi: { color: 'green', text: 'Multi Interface' }
    };
    const config = typeMap[scenarioType as keyof typeof typeMap] || { color: 'default', text: scenarioType };
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  const executionColumns = [
    {
      title: 'Execution Name',
      dataIndex: 'execution_name',
      key: 'execution_name',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => getStatusTag(status),
    },
    {
      title: 'Total Requests',
      dataIndex: 'total_requests',
      key: 'total_requests',
    },
    {
      title: 'Failures',
      dataIndex: 'total_failures',
      key: 'total_failures',
    },
    {
      title: 'Avg Response Time (ms)',
      dataIndex: 'avg_response_time',
      key: 'avg_response_time',
      render: (time: number) => time.toFixed(2),
    },
    {
      title: 'RPS',
      dataIndex: 'requests_per_second',
      key: 'requests_per_second',
      render: (rps: number) => rps.toFixed(2),
    },
    {
      title: 'Error Rate',
      dataIndex: 'error_rate',
      key: 'error_rate',
      render: (rate: number) => `${(rate * 100).toFixed(2)}%`,
    },
    {
      title: 'Started At',
      dataIndex: 'started_at',
      key: 'started_at',
      render: (date: string) => date ? new Date(date).toLocaleString() : '-',
    },
    {
      title: 'Completed At',
      dataIndex: 'completed_at',
      key: 'completed_at',
      render: (date: string) => date ? new Date(date).toLocaleString() : '-',
    },
    {
      title: 'Actions',
      key: 'action',
      render: (_: any, record: TestExecution) => (
        <Space>
          {record.status === 'pending' && (
            <Button
              size="small"
              type="primary"
              icon={<PlayCircleOutlined />}
              onClick={() => {
                testExecutionService.startTestExecution(record.id).then(() => {
                  message.success('Execution started');
                  fetchExecutions();
                }).catch(() => {
                  message.error('Failed to start execution');
                });
              }}
            >
              Start
            </Button>
          )}
          {record.status === 'running' && (
            <Button
              size="small"
              danger
              icon={<PauseCircleOutlined />}
              onClick={() => {
                testExecutionService.stopTestExecution(record.id, { reason: 'Manual stop' }).then(() => {
                  message.success('Execution stopped');
                  fetchExecutions();
                }).catch(() => {
                  message.error('Failed to stop execution');
                });
              }}
            >
              Stop
            </Button>
          )}
          <Button
            size="small"
            icon={<ReloadOutlined />}
            onClick={() => navigate(`/test-executions/${record.id}`)}
          >
            View Details
          </Button>
        </Space>
      ),
    },
  ];

  if (!testTask) {
    return <div>Loading...</div>;
  }

  const latestExecution = executions[0];
  const successRate = latestExecution ? 
    ((latestExecution.total_requests - latestExecution.total_failures) / latestExecution.total_requests * 100) : 0;

  // 场景创建步骤内容
  const renderScenarioSteps = () => {
    const steps = [
      {
        title: 'Script Upload',
        content: (
          <div>
            <Title level={4}>Upload Locust Script</Title>
            <Space direction="vertical" style={{ width: '100%' }}>
              <Upload
                accept=".py"
                beforeUpload={handleFileUpload}
                showUploadList={false}
              >
                <Button icon={<UploadOutlined />} size="large">
                  Upload Locust File
                </Button>
              </Upload>
              {locustFile && (
                <div>
                  <Text>Selected file: {locustFile.name}</Text>
                  <Button 
                    type="link" 
                    icon={<EditOutlined />}
                    onClick={() => setEditorModalVisible(true)}
                  >
                    Edit Script
                  </Button>
                </div>
              )}
              <Button 
                type="dashed" 
                icon={<FileTextOutlined />}
                onClick={() => setEditorModalVisible(true)}
                style={{ width: '100%' }}
              >
                Create New Script
              </Button>
            </Space>
          </div>
        )
      },
      {
        title: 'Strategy Configuration',
        content: (
          <div>
            <Title level={4}>Select Test Strategy</Title>
            <Row gutter={16}>
              {strategies.map(strategy => (
                <Col span={8} key={strategy.id}>
                  <Card
                    hoverable
                    style={{ 
                      border: selectedStrategy?.id === strategy.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                      cursor: 'pointer'
                    }}
                    onClick={() => setSelectedStrategy(strategy)}
                  >
                    <div style={{ textAlign: 'center' }}>
                      <SettingOutlined style={{ fontSize: 24, color: '#1890ff', marginBottom: 8 }} />
                      <Title level={5}>{strategy.name}</Title>
                      <Text type="secondary">{strategy.strategy_type}</Text>
                      <div style={{ marginTop: 8 }}>
                        <Text>Users: {strategy.user_count}</Text><br/>
                        <Text>Spawn Rate: {strategy.spawn_rate}/s</Text><br/>
                        <Text>Run Time: {strategy.run_time}s</Text>
                      </div>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </div>
        )
      },
      {
        title: 'Load Generator Configuration',
        content: (
          <div>
            <Title level={4}>Select Load Generator & Configuration</Title>
            <Row gutter={16}>
              <Col span={12}>
                <Title level={5}>Load Generators</Title>
                <Space direction="vertical" style={{ width: '100%' }}>
                  {loadGenerators.map(lg => (
                    <Card
                      key={lg.id}
                      size="small"
                      hoverable
                      style={{ 
                        border: selectedLoadGenerator?.id === lg.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                        cursor: 'pointer'
                      }}
                      onClick={() => {
                        setSelectedLoadGenerator(lg);
                        loadLoadGeneratorConfigs(lg.id);
                      }}
                    >
                      <Space>
                        <CloudServerOutlined />
                        <div>
                          <Text strong>{lg.name}</Text><br/>
                          <Text type="secondary">{lg.host}:{lg.port}</Text>
                        </div>
                      </Space>
                    </Card>
                  ))}
                </Space>
              </Col>
              <Col span={12}>
                <Title level={5}>Configurations</Title>
                <Space direction="vertical" style={{ width: '100%' }}>
                  {loadGeneratorConfigs.map(config => (
                    <Card
                      key={config.id}
                      size="small"
                      hoverable
                      style={{ 
                        border: selectedConfig?.id === config.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                        cursor: 'pointer'
                      }}
                      onClick={() => setSelectedConfig(config)}
                    >
                      <Space>
                        <InfoCircleOutlined />
                        <div>
                          <Text strong>{config.config_name}</Text><br/>
                          <Text type="secondary">CPU: {config.system_cpu_cores} cores, Memory: {config.system_memory_gb}GB</Text>
                        </div>
                      </Space>
                    </Card>
                  ))}
                </Space>
              </Col>
            </Row>
          </div>
        )
      },
      {
        title: 'Execute',
        content: (
          <div style={{ textAlign: 'center', padding: '40px 0' }}>
            <Title level={4}>Ready to Execute</Title>
            <Space direction="vertical" size="large">
              <div>
                <Text>Strategy: {selectedStrategy?.name}</Text><br/>
                <Text>Load Generator: {selectedLoadGenerator?.name}</Text><br/>
                <Text>Configuration: {selectedConfig?.config_name}</Text>
              </div>
              <Button 
                type="primary" 
                size="large"
                icon={<PlayCircleOutlined />}
                onClick={handleExecuteScenario}
              >
                Execute Test
              </Button>
            </Space>
          </div>
        )
      }
    ];

    return steps[currentStep]?.content;
  };

  return (
    <div style={{ padding: '24px' }}>
      {/* 页面头部 */}
      <div style={{ marginBottom: '24px' }}>
        <Space>
          <Button 
            icon={<ArrowLeftOutlined />}
            onClick={() => navigate('/test-tasks')}
          >
            Back
          </Button>
          <Title level={2} style={{ margin: 0 }}>{testTask.name}</Title>
          {getScenarioTypeTag(testTask.scenario_type)}
        </Space>
      </div>

      {/* 测试任务基本信息 */}
      <Card style={{ marginBottom: '24px' }}>
        <Title level={3}>Test Task Information</Title>
        <Row gutter={24}>
          <Col span={6}>
            <div style={{ marginBottom: 16 }}>
              <Text strong>Task Name:</Text><br/>
              <Text>{testTask.name}</Text>
            </div>
          </Col>
          <Col span={6}>
            <div style={{ marginBottom: 16 }}>
              <Text strong>Target Host:</Text><br/>
              <Text>{testTask.target_host}</Text>
            </div>
          </Col>
          <Col span={6}>
            <div style={{ marginBottom: 16 }}>
              <Text strong>Scenario Type:</Text><br/>
              {getScenarioTypeTag(testTask.scenario_type)}
            </div>
          </Col>
          <Col span={6}>
            <div style={{ marginBottom: 16 }}>
              <Text strong>Created At:</Text><br/>
              <Text>{new Date(testTask.created_at).toLocaleString()}</Text>
            </div>
          </Col>
        </Row>
        {testTask.description && (
          <div>
            <Text strong>Description:</Text><br/>
            <Text>{testTask.description}</Text>
          </div>
        )}
      </Card>

      {/* 测试场景管理区域 */}
      <Card style={{ marginBottom: '24px' }}>
        <Row gutter={0} style={{ minHeight: '600px' }}>
          {/* 左侧场景列表 */}
          <Col span={6} style={{ borderRight: '1px solid #f0f0f0', paddingRight: '16px' }}>
            <div style={{ marginBottom: '16px' }}>
              <Title level={4} style={{ margin: 0, color: '#fff' }}>Test Scenarios</Title>
            </div>

            {/* 场景列表 */}
            <div style={{ marginBottom: '16px' }}>
              {testTask.scenarios && testTask.scenarios.length > 0 ? (
                <div>
                  {testTask.scenarios.map((scenario, index) => (
                    <div
                      key={scenario.id}
                      style={{
                        padding: '8px 12px',
                        marginBottom: '8px',
                        background: '#1f1f1f',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        border: '1px solid #333',
                        color: '#ff4d4f'
                      }}
                    >
                      <Text style={{ color: '#ff4d4f' }}>场景{index + 1}</Text>
                    </div>
                  ))}
                </div>
              ) : (
                <div style={{ color: '#999', fontSize: '12px' }}>
                  No scenarios yet
                </div>
              )}
            </div>
          </Col>

          {/* 右侧场景创建和配置区域 */}
          <Col span={18} style={{ paddingLeft: '24px' }}>
            {/* 顶部步骤指示器 */}
            <div style={{ 
              background: '#1f1f1f', 
              padding: '12px 16px', 
              borderRadius: '4px', 
              marginBottom: '24px',
              border: '1px solid #333'
            }}>
              <Text style={{ color: '#ff4d4f', fontSize: '14px' }}>
                脚本上传编写调试 {'>>'} 策略配置 {'>>'} 压力机选择和配置 {'>>'} 执行
              </Text>
            </div>

            {/* 操作按钮 */}
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '24px' }}>
              <Space>
                <Button 
                  type="primary"
                  icon={<PlayCircleOutlined />}
                  onClick={handleCreateScenario}
                >
                  Create Scenario
                </Button>
                <Button 
                  icon={<ReloadOutlined />}
                  onClick={fetchTestTask}
                >
                  Refresh
                </Button>
              </Space>
            </div>

            {/* 主要内容区域 */}
            <div style={{ 
              minHeight: '400px', 
              background: '#1a1a1a',
              borderRadius: '8px',
              border: '1px solid #333',
              padding: '24px'
            }}>
              {isCreatingScenario ? (
                <div>
                  {/* 步骤指示器 */}
                  <Steps current={currentStep} style={{ marginBottom: 24 }}>
                    <Step title="Script Upload" />
                    <Step title="Strategy Config" />
                    <Step title="Load Generator" />
                    <Step title="Execute" />
                  </Steps>

                  {/* 步骤内容 */}
                  <div style={{ minHeight: 300 }}>
                    {renderScenarioSteps()}
                  </div>

                  {/* 操作按钮 */}
                  <div style={{ textAlign: 'right', marginTop: 24, borderTop: '1px solid #333', paddingTop: 16 }}>
                    <Space>
                      <Button onClick={handleCancelScenario}>
                        Cancel
                      </Button>
                      {currentStep > 0 && (
                        <Button onClick={handlePrevStep}>
                          Previous
                        </Button>
                      )}
                      {currentStep < 3 && (
                        <Button 
                          type="primary" 
                          onClick={handleNextStep}
                          disabled={
                            (currentStep === 0 && !locustFile && !editorContent) ||
                            (currentStep === 1 && !selectedStrategy) ||
                            (currentStep === 2 && (!selectedLoadGenerator || !selectedConfig))
                          }
                        >
                          Next
                        </Button>
                      )}
                      {currentStep === 3 && (
                        <Button 
                          type="primary" 
                          onClick={handleExecuteScenario}
                          disabled={!selectedStrategy || !selectedLoadGenerator || !selectedConfig}
                        >
                          Execute Test
                        </Button>
                      )}
                    </Space>
                  </div>
                </div>
              ) : (
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  height: '100%'
                }}>
                  <div style={{ textAlign: 'center', color: '#999' }}>
                    <FileTextOutlined style={{ fontSize: 64, marginBottom: 16, color: '#666' }} />
                    <div style={{ fontSize: '16px', marginBottom: 8 }}>No scenarios created yet</div>
                    <Button 
                      type="primary" 
                      size="large"
                      onClick={handleCreateScenario}
                    >
                      Create Your First Scenario
                    </Button>
                  </div>
                </div>
              )}
            </div>
          </Col>
        </Row>
      </Card>

      {/* 执行记录 */}
      <Card>
        <Title level={3}>Execution History</Title>
        <Table
          columns={executionColumns}
          dataSource={executions}
          rowKey="id"
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `Total ${total} records`,
          }}
        />
      </Card>


      {/* 脚本编辑器模态框 */}
      <Modal
        title="Script Editor"
        open={editorModalVisible}
        onCancel={() => setEditorModalVisible(false)}
        footer={null}
        width={1000}
        style={{ top: 20 }}
      >
        <div style={{ marginBottom: 16 }}>
          <Text>Edit your Locust script below:</Text>
        </div>
        <Input.TextArea
          value={editorContent}
          onChange={(e) => setEditorContent(e.target.value)}
          placeholder="Enter your Locust script here..."
          rows={20}
          style={{ fontFamily: 'monospace' }}
        />
        <div style={{ textAlign: 'right', marginTop: 16 }}>
          <Space>
            <Button onClick={() => setEditorModalVisible(false)}>
              Cancel
            </Button>
            <Button type="primary" onClick={handleEditorSave}>
              Save Script
            </Button>
          </Space>
        </div>
      </Modal>

      {/* 创建执行模态框 */}
      <Modal
        title="Create Test Execution"
        open={executionModalVisible}
        onCancel={() => setExecutionModalVisible(false)}
        footer={null}
        width={600}
      >
        <Form
          layout="vertical"
          onFinish={handleCreateExecution}
        >
          <Form.Item
            name="strategy_id"
            label="Test Strategy"
            rules={[{ required: true, message: 'Please select test strategy' }]}
          >
            <Select placeholder="Select test strategy">
              {strategies.map(strategy => (
                <Select.Option key={strategy.id} value={strategy.id}>
                  {strategy.name} ({strategy.strategy_type})
                </Select.Option>
              ))}
            </Select>
          </Form.Item>

          <Form.Item
            name="load_generator_id"
            label="Load Generator"
            rules={[{ required: true, message: 'Please select load generator' }]}
          >
            <Select 
              placeholder="Select load generator"
              onChange={(value) => loadLoadGeneratorConfigs(value)}
            >
              {loadGenerators.map(lg => (
                <Select.Option key={lg.id} value={lg.id}>
                  {lg.name} ({lg.host})
                </Select.Option>
              ))}
            </Select>
          </Form.Item>

          <Form.Item
            name="load_generator_config_id"
            label="Load Generator Config"
            rules={[{ required: true, message: 'Please select load generator config' }]}
          >
            <Select placeholder="Select load generator config">
              {loadGeneratorConfigs.map(config => (
                <Select.Option key={config.id} value={config.id}>
                  {config.config_name}
                </Select.Option>
              ))}
            </Select>
          </Form.Item>

          <Form.Item
            name="execution_name"
            label="Execution Name"
          >
            <Input placeholder="Enter execution name (optional)" />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                Create Execution
              </Button>
              <Button onClick={() => setExecutionModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

// 使用 React.memo 优化组件性能，避免不必要的重渲染
export default React.memo(TestTaskDetail);
